      {/* ISSO É PARA O CASO DE EU QUERER UM DADO QUE EU VÁ TRABALHAR COM ELE */}
      {/* {state === "" ? (
        ""
      ) : ( */}
      {/* ISSO É PARA O RETORNO DE UMA STRING APENAS */}
      {/* <Grid> */}
      <AnimatePresence>
        {state.map((d, idx) => (
          <Bloco
            // pra mostrar item por item preciso da key
            key={d}
            whileHover={{ backgroundColor: "green" }}
            animate={{
              scale: [1, 1.5, 1],
              // rotate: [10, 0, 0],
              transition: {
                delay: 0.2 * idx,
                duration: 0.5,
                opacity: 0.2 * idx,
              },
            }}
          >
            {d}
          </Bloco>
        ))}
      </AnimatePresence>
      {/* </Grid> */}
    </>
  );
}

const Grid = styled.div`
  display: flex;
`;

const Bloco = styled(motion.div)`
  display: flex;
  flex-direction: column;
  justifycontent: space-between;
  align-items: center;
  background-color: #01573d;
  padding: 20px; /* this */
  margin: 20px;
  transition: 1s background-color;
  flex-grow: 1;
`;

export default Projects;

