import { useState, FormEvent, useEffect } from "react";
import { useHistory } from "react-router-dom";
import api from "../../services/api";
import "./style.css";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Fingerprint from "@material-ui/icons/Fingerprint";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Footer from "../../components/footer/footer";
import { Navbartop } from "../../components/navbartop/navbartest";
import SidebarDashboard from "../../components/sidebar";
import barrinhaService from "../../services/barrinhaState";
import styled from "@emotion/styled";
import { AnimatePresence, AnimateSharedLayout, motion } from "framer-motion";

const UseStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(10),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%",
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

function CadastroProject() {
  // O userState guarda váriaveis dinamicamente
  const [nomeLab, setnomeLab] = useState("");
  // variavel auxiliar que funciona como histório de navegação
  const history = useHistory();
  const classes = UseStyles();
  const enviar = async (e: FormEvent) => {
    e.preventDefault();

    // pegar a hora atual
    const time = new Date();
    const data = {
      nomeLab,
      time,
    };
    console.log(data);

    // POST QUANDO MINHA API ESTIVER ONLINE
    // SE TEM BARRINHA NO BACK TEM BNARRINHA NO FRONT
    const response = await api
      .post("/laboratory/", data)
      // depois que rodar o post, roda o then
      // CASO SUCESSO
      .then(() => {
        alert("Cadastrado com sucesso");

        // como eu nao sei se ele acessou a pagina home, estou forçando
        // ele ir pra home
        history.push("/");
      })
      // CASO ERRO
      .catch(() => {
        alert("Serviço já existente");
      });
  };

  const [collapsed, setCollapsed] = useState(true);

  useEffect(() => {
    const subscribe = barrinhaService.onBarrinha().subscribe((state) => {
      if (state) {
        setCollapsed(false);
      } else {
        setCollapsed(true);
      }
    });
  });

  return (
    // importante, sempre retornar um componente
    // exemplo: ou uma div inteira, ou um h1
    // tambem posso só abrir ou fechar uma tag <></>

    <>
      <SidebarDashboard />
      <Container_animate>
        <AnimatePresence>
          <Bloco
            initial={{ marginLeft: 200 }}
            animate={{ marginLeft: collapsed ? 64 : 168 }}
          >
            <CssBaseline />
            <div className={classes.paper}>
              <Avatar className={classes.avatar}>
                <Fingerprint />
              </Avatar>
              <Typography component="h1" variant="h5">
                Sala de aula
              </Typography>
            </div>

            <form className={classes.form} noValidate>
              <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                id="nomeLab"
                label="nome do Serviço"
                name="email"
                autoComplete="nomeLab do Serviço"
                autoFocus
                onChange={(e) => setnomeLab(e.target.value)}
                type="text"
              />
            </form>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
              // onCLinc chama a função enviar criada la em cima
              onClick={enviar}
            >
              Autenticação
            </Button>
          </Bloco>
        </AnimatePresence>
      </Container_animate>

      <Footer />
    </>
  );
}

const Container_animate = styled.div`
  display: flex;
  justifycontent: center;
  flex-direction: column;
  align-items: center;
`;

const Bloco = styled(motion.div)`
  display: flex;
  flex-direction: column;
  justifycontent: center;
  align-items: center;
  background-color: #01573d;
  padding: 20px; /* this */
  margin: 20px;
  transition: 1s background-color;
  // flex-grow: 1;
  width: 40%;
  // height: 20%;
`;

export default CadastroProject;
