                  type="text"
                />

                <TextField
                  variant="outlined"
                  margin="normal"
                  fullWidth
                  id="select"
                  label="Imagens Disponiveis"
                  value=""
                  select
                >
                  <MenuItem value="">
                    <em>Nenhum</em>
                  </MenuItem>
                  <MenuItem value="1">Ubuntu 20.04</MenuItem>
                  <MenuItem value="2">Windows XP</MenuItem>
                  <MenuItem value="3">CentOS 8</MenuItem>
                </TextField>
              </Grid>
              <Grid item xs={8}>
                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  color="primary"
                  className={classes.submit}
                  // onCLinc chama a função enviar criada la em cima
                  onClick={enviar}
                >
                  Criar Laboratorio Virtual
                </Button>
              </Grid>
            </Grid>
            <Bloco
              initial={{ marginLeft: 200 }}
              animate={{ marginLeft: collapsed ? 64 : 168 }}
            >
            </Bloco>
        </form>
      </AnimatePresence>
    </Container_animate>
    </>
  );
